export const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    cartItems: [],
  },
  reducers: {
    // addItemToCart: (state, action) => {
    //     state.cartItems.push({
    //         itemId: action.payload.eachProduct.id,
    //         itemQuantity: action.payload.quantity
    //     })
    // }
    addItemToCart: (state, action) => {
        state.cart.cartItems.find(item => action.payload.eachProduct.id === item.id);
        // const alreadyInCart = state.cartItems.find(item => action.payload.eachProduct.id === item.id);
        console.log(item.id)
    }

}
},
)

export const getCartItems = state => state.cart.cartItems;
