import { createSlice } from '@reduxjs/toolkit'

export const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    cartItems: [],
  },
  reducers: {
    // addItemToCart: (state, action) => {
    //     state.cartItems.push({
    //         itemId: action.payload.eachProduct.id,
    //         itemQuantity: action.payload.quantity
    //     })
    // }
    addItemToCart: (state, action) => {
        const alreadyInCart = state.cart.cartItems.find(cartItem => action.payload.eachProduct.id === cartItem.id)
        if(alreadyInCart) {
            state.cartItems.find(cartItem => action.payload.eachProduct.id === cartItem.id)(
                itemQuantity
            )
        }
    }

    }
})

export const getCartItems = state => state.cart.cartItems;
export const { addItemToCart } = cartSlice.actions;
export default cartSlice.reducer;
