import { createSlice } from '@reduxjs/toolkit'

export const goodsSlice = createSlice({
  name: 'goods',
  initialState: {
    selectedCategory: 'ХЛЕБ'
  },
  reducers: {
    filterCategory
  },
})

export const getSelectedCategory = state => state.goods.selectedCategory;

export default goodsSlice.reducer
