import { getCartItems, getTotalPrice } from "../redux/cartSlice";
import { useSelector } from "react-redux";

export const BackToCart = () => {
    const cartItems = useSelector(getCartItems);
    const totalPrice = useSelector(getTotalPrice);

    return(

            <button onClick={() => alert('Hey')} className="backToCartBtn">
                <span tooltip="I'm up above it!"><p className="backToCartText">Перейти в корзину</p></span>
                <img className="backToCartImg" src="./cart-loaded.png" alt="backToCartBtn"></img>
                <span className="backToCartTotalPrice">{cartItems.length > 0 ? totalPrice + ' руб.': null}</span>
            </button>
    )
}
